DROP TABLE IF EXISTS NCMS_AUTOPECAS;

CREATE TABLE IF NOT EXISTS NCMS_AUTOPECAS(
    CODIGO INT(11) AUTO_INCREMENT NOT NULL,
    NCM INT(11) NOT NULL,
    CEST INT(11) DEFAULT 0 NOT NULL,
	UF_ORIGEM CHAR(2) DEFAULT 'SP', 
	UF_DESTINO CHAR(2) DEFAULT 'SP',  
    PRIMARY KEY(CODIGO)
);


	
	