-- TABELA "AUXLIAR", com todos os NCMS monofasicos
DROP TABLE IF EXISTS NCMS_NOVEMBRO_VENDA;

CREATE TABLE IF NOT EXISTS NCMS_NOVEMBRO_VENDA(
    CODIGO INT(11) AUTO_INCREMENT NOT NULL,
    NCM INT(11) NOT NULL,
    EXCECAO INT(11) DEFAULT 0 NOT NULL,
    PRIMARY KEY(CODIGO)
);

