CREATE TABLE IF NOT EXISTS NCMS_MONOFASICOS(
    CODIGO INT(11) AUTO_INCREMENT NOT NULL,
    NCM INT(11) NOT NULL,
    EXCECAO INT(11) DEFAULT 0 NOT NULL,
    PRIMARY KEY(CODIGO)
);

